import React, {Component, Fragment} from "react"
import jsSHA from "jssha"
import "../css/login.css"
import axios from "axios"

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            loginFailed: false
        };

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleUserChange = this.handleUserChange.bind(this);
    }

    handleUserChange(e) {
        this.setState({
            [e.target.name]: e.target.value,
            loginFailed: false
        }, () => {
            console.log(this.state)
        });
    }


    handleSubmit(e) {
        e.preventDefault();

        const user = this.state.username;
        const password = this.state.password;

        const hmac = new jsSHA('SHA-256', 'TEXT');

        hmac.setHMACKey(password, 'TEXT');
        hmac.update(user);
        hmac.update(Date.now().toString(36).substring(0, 4));

        const token = `${hmac.getHMAC('HEX')}%${user}`;

        const api = axios.create({
            baseURL: 'http://45.77.58.134:8080',
            headers: {'Authorization': 'Bearer ' + token}
        });

        (async () => {
            try {
                const res = await api.get('/clients');
                this.props.setToken(token);
                this.props.history.push("/home", {
                    token: token
                })

            } catch (e) {
                // network error - could be 401 or other "not ok" HTTP status
                this.setState({
                    loginFailed: true
                })
            }
        })();

    }

    render() {

        return (
            <Fragment>
                <div>
                    <div className="login-form">
                        <form onSubmit={this.handleSubmit}>
                            <h2 className="text-center">Log In</h2>

                            <label htmlFor="username">Username:</label>
                            <input type="text" className="form-control" placeholder="Enter Username"
                                   name="username"
                                   onChange={this.handleUserChange}
                                   value={this.state.username}/>
                            <br/>
                            <label htmlFor="password">Password:</label>
                            <input type="password" className="form-control" placeholder="Enter Password"
                                   name="password"
                                   onChange={this.handleUserChange}
                                   value={this.state.password}/>
                            <br/>
                            {this.state.loginFailed && <span style={{color: "red"}}>Login failed</span>}
                            <button className="btn btn-primary btn-block">Log in</button>

                        </form>
                        <p className="text-center"><a href="Register.html">Create an Account</a></p>
                    </div>
                </div>
            </Fragment>
        )
    }
}


export default Login
